mapper.select:
SELECT COUNT(id) AS USER_CNT
FROM   USERS
WHERE  email = #{email}

mapper.insert:
INSERT INTO USERS (
      id
    , role
    , status
    , email
    , password
    , name
    , nickname
    , phone
    , created_at
    , updated_at
) VALUES (
      #{id}
    , #{role}
    , #{status}
    , #{email}
    , #{password}
    , #{name}
    , #{nickname}
    , #{phone}
    , CURRENT_TIMESTAMP
    , CURRENT_TIMESTAMP
)
